{
    "query": "select daily_cohort, weekly_cohort, to_char(cast(daily_cohort as timestamp), 'YYYY-MM-01') as monthly_cohort, CASE WHEN platform <> '' THEN platform WHEN (platform = '' AND campaign_name ilike '%os%') THEN 'ios' WHEN (platform = '' AND (campaign_name ilike '%android%' OR campaign_name like '%_ADR_%')) THEN 'android' ELSE 'other' END, CASE WHEN product_name is null THEN 'App Unknown' ELSE product_name END, CASE WHEN campaign_name is null THEN 'Campaign Unknown' ELSE campaign_name END, CASE WHEN adset_name is null THEN 'Adset Unknown' ELSE adset_name END, channel, campaign_id, CASE WHEN campaign_tag is null THEN 'Untagged' ELSE campaign_tag END, CASE WHEN campaign_subtag is null THEN 'Untagged' ELSE campaign_subtag END, adset_tag, adset_tag, event_name, CASE WHEN event_name = 'af_purchase' THEN 'iapr' WHEN event_name like 'ad_%' THEN 'iaar' ELSE null END, spend, cohort_events, cohort_revenue, event_day, event_week, mmp from \"COMPANY\".\"performance_cube_filtered\" where daily_cohort >= current_timestamp - interval '90 day'",
    "description": "Dump 90 days of COMPANY's cube"
}

{
    "composition": {
    	"queryType": "cube",
        "table": "performance_cube_filtered",
        "parameters": {
        	"interval": { "value": 30, "unit": "day" },
        	"rowFilters": [
        		{
	        		"metadata": {
	        			"templateType": "row_filter",
	        			"identifier": "test"
	        		},
	        		"or": [
	        			{
	        				"column": "mmp",
	        				"operator": "not_equal",
	        				"value": "Google"
	        			},
	        			{
	        				"column": "mmp",
	        				"operator": "is_null",
	        				"value": null
	        			}
        			]
        		}
        	]
        },
        "columns": {
            "daily_cohort": {
                "sql": "daily_cohort"
            },
            "weekly_cohort": {
                "sql": "weekly_cohort"
            },
            "monthly_cohort": {
                "sql": "to_char(cast(daily_cohort as timestamp), 'YYYY-MM-01') as monthly_cohort"
            },
            "platform": {
                "cases": [
                    {
                        "when": {
                            "operator": "not_equal",
                            "column": "platform",
                            "value": ""
                        },
                        "then": {
                            "column": "platform"
                        }
                    },
                    {
                        "and": [
                            {
                                "when": {
                                    "operator": "equal",
                                    "column": "platform",
                                    "value": ""
                                }
                            },
                            {
                                "when": {
                                    "operator": "contains",
                                    "column": "campaign_name",
                                    "value": "ios"
                                }
                            }
                        ],
                        "then": {
                            "value": "ios"
                        }
                    },
                    {
                        "and": [
                            {
                                "when": {
                                    "operator": "equal",
                                    "column": "platform",
                                    "value": ""
                                }
                            },
                            {
                                "or": [
                                    {
                                        "when": {
                                            "operator": "contains",
                                            "column": "campaign_name",
                                            "value": "android"
                                        }
                                    },
                                    {
                                        "when": {
                                            "operator": "contains",
                                            "column": "campaign_name",
                                            "value": "_ADR_",
                                            "options": {
                                                "caseSensitive": true
                                            }
                                        }
                                    }
                                ]
                            }
                        ],
                        "then": {
                            "value": "android"
                        }
                    },
                    {
                        "default": {
                            "value": "other"
                        }
                    }
                ]
            },
            "product_name": {
                "cases": [
                	{
	                	"or": [
	                		{
		                        "when": {
		                            "operator": "equal",
		                            "column": "product_name",
		                            "value": ""
		                        }
	                		},
	                		{
		                        "when": {
		                            "operator": "is_null",
		                            "column": "product_name",
		                            "value": null
		                        }
	                		}
	                    ],
	                    "then": {
                        	"value": "Other"
                        }
            		},
                    {
                        "default": {
                            "column": "product_name"
                        }
                    }
                ]
            },
            "campaign_name": {
                "cases": [
                    {
                        "when": {
                            "operator": "is_null",
                            "column": "campaign_name",
                            "value": null
                        },
                        "then": {
                            "value": "Campaign Unknown"
                        }
                    },
                    {
                        "default": {
                            "column": "campaign_name"
                        }
                    }
                ]
            },
            "adset_name": {
                "cases": [
                    {
                        "when": {
                            "operator": "is_null",
                            "column": "adset_name",
                            "value": null
                        },
                        "then": {
                            "value": "Adset Unknown"
                        }
                    },
                    {
                        "default": {
                            "column": "adset_name"
                        }
                    }
                ]
            },
            "channel": {
                "sql": "channel"
            },
            "campaign_id": {
                "sql": "campaign_id"
            },
            "campaign_tag": {
                "cases": [
                    {
                        "when": {
                            "operator": "is_null",
                            "column": "campaign_tag",
                            "value": null
                        },
                        "then": {
                            "value": "Untagged"
                        }
                    },
                    {
                        "default": {
                            "column": "campaign_tag"
                        }
                    }
                ]
            },
            "campaign_subtag": {
                "cases": [
                    {
                        "when": {
                            "operator": "is_null",
                            "column": "campaign_subtag",
                            "value": null
                        },
                        "then": {
                            "value": "Untagged"
                        }
                    },
                    {
                        "default": {
                            "column": "campaign_subtag"
                        }
                    }
                ]
            },
            "adset_tag": {
                "sql": "adset_tag"
            },
            "adset_subtag": {
                "sql": "adset_subtag"
            },
            "event_name": {
                "sql": "event_name"
            },
            "revenue_type": {
                "cases": [
                    {
                        "when": {
                            "operator": "equal",
                            "column": "event_name",
                            "value": "af_purchase"
                        },
                        "then": {
                            "value": "iapr"
                        }
                    },
                    {
                        "when": {
                            "operator": "starts_with",
                            "column": "event_name",
                            "value": "ad_",
                            "options": {
                                "caseSensitive": true
                            }
                        },
                        "then": {
                            "value": "iaar"
                        }
                    }
                ]
            },
            "spend": {
                "sql": "spend"
            },
            "cohort_events": {
                "sql": "cohort_events"
            },
            "cohort_revenue": {
                "sql": "cohort_revenue"
            },
            "event_day": {
                "sql": "event_day"
            },
            "event_week": {
                "sql": "event_week"
            },
            "mmp": {
                "sql": "mmp"
            }
        }
    },
    "description": "Standard Performance Cube query for all clients."
}

